mapper.select:
SELECT m.*,group_concat(g.genre_title) as genre FROM movie_info m inner join movie_genre mg ON mg.movie_id = m.movie_id inner join genre_info g ON mg.genre_id = g.genre_id group by m.movie_id
HAVING replace(movie_title,' ','') like "${searchWord}%"
order by m.opening_date desc LIMIT ${page}

SELECT * FROM user_info
WHERE replace(user_id,' ','') like "${searchWord}%"
order by join_date desc LIMIT ${page}

SELECT * FROM diary_info
WHERE replace(user_id,' ','') like "${searchWord}%"
ORDER BY reg_date desc LIMIT ${page}

SELECT mc.*,m.movie_title FROM movie_comment mc INNER JOIN movie_info m ON mc.movie_id = m.movie_id
WHERE replace(mc.user_id,' ','') like "${searchWord}%"
ORDER BY mc.comment_reg_date LIMIT ${page}

SELECT * FROM user_qna
WHERE replace(user_id,' ','') like "${searchWord}%"
ORDER BY qna_status asc,qna_reg_date desc LIMIT ${page}

SELECT im.*,sum(nvl(iu.indie_like,"0")) as indie_like,sum(nvl(iu.indie_hate,"0")) as indie_hate FROM indie_movie_info im left outer join indie_user iu ON im.indie_id = iu.indie_id
WHERE replace(im.indie_title,' ','') like "${searchWord}%"
GROUP BY im.indie_id ORDER BY im.indie_confirm asc,im.indie_update_date desc LIMIT ${page}
